component.schema:
{
 "properties": {
  "spec": {
   "properties": {
    "blockSize": {
     "description": "The block size to use for IP address assignments from this pool. Defaults to 26 for IPv4 and 112 for IPv6.",
     "type": "integer"
    },
    "cidr": {
     "description": "The pool CIDR.",
     "type": "string"
    },
    "disabled": {
     "description": "When disabled is true, IPAM will not assign addresses from this pool.",
     "type": "boolean"
    },
    "dns": {
     "description": "DNS contains values interesting for DNS resolvers",
     "properties": {
      "domain": {
       "type": "string"
      },
      "nameservers": {
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "options": {
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "search": {
       "items": {
        "type": "string"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "gateway": {
     "type": "string"
    },
    "rangeEnd": {
     "description": "The last ip, inclusive",
     "type": "string"
    },
    "rangeStart": {
     "description": "The first ip, inclusive",
     "type": "string"
    },
    "routes": {
     "items": {
      "properties": {
       "dst": {
        "type": "string"
       },
       "gateway": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "type": {
     "type": "string"
    },
    "vlanConfig": {
     "properties": {
      "master": {
       "type": "string"
      },
      "vlanId": {
       "format": "int32",
       "type": "integer"
      }
     },
     "required": [
      "master",
      "vlanId"
     ],
     "type": "object"
    }
   },
   "required": [
    "cidr",
    "type"
   ],
   "type": "object"
  }
 },
 "title": "IP Pool",
 "type": "object"
}
